00255 void cppMD01Frame::PopulateMasterGrid()
00256 {
00257     // Attempt to connect to the MySQL database
00258     if (!conn.connect("bcswebtools", "localhost", "bcs", "Peace007!amb"))
00259     {
00260         // Database connection failed, exit the function
00261         // wxWrite("Database connection failed.");
00262         return;
00263     }
00264 
00265     // Execute the SQL query to fetch all records from the 'lk1' table, ordered by 'cat'
00266     mysqlpp::Query query = conn.query("SELECT * FROM lk1 ORDER BY cat");
00267     mysqlpp::StoreQueryResult res = query.store();
00268 
00269     // Clear the current contents of the grid and append rows based on the number of records fetched
00270     masterGrid->ClearGrid();
00271     masterGrid->AppendRows(res.num_rows());
00272 
00273     // Populate the grid with data from the result set
00274     for (size_t i = 0; i < res.num_rows(); ++i)
00275     {
00276         masterGrid->SetCellValue(i, 0, res[i][0].c_str());  // Set cell value for column 0
00277         masterGrid->SetCellValue(i, 1, res[i][1].c_str());  // Set cell value for column 1
00278         masterGrid->SetCellValue(i, 2, res[i][2].c_str());  // Set cell value for column 2
00279     }
00280 }
