00290 void cppMD01Frame::OnCellClick(wxGridEvent& event)
00291 {
00292     // Get the grid from the event
00293     wxGrid* grid = static_cast<wxGrid*>(event.GetEventObject());
00294 
00295     // Get the clicked cell's row and column
00296     row = event.GetRow();
00297     int col = event.GetCol();
00298 
00299     // Retrieve the cell value as a wxString
00300     wxString cellValue = grid->GetCellValue(row, col);
00301 
00302     // Check if the cell value starts with "http"
00303     if (cellValue.StartsWith("http"))
00304     {
00305         // Open the URL in the default web browser
00306         wxLaunchDefaultBrowser(cellValue);
00307     }
00308     else if (cellValue.find(".pdf") != wxNOT_FOUND)
00309     {
00310         cmd = "okular \"";
00311         std::string tbuf;
00312         tbuf = cellValue.ToStdString();
00313         cmd = cmd + tbuf + "\"";
00314         system(cmd.c_str());
00315     }
00316     else if (cellValue.find(".odt")  != wxNOT_FOUND)
00317     {
00318         std::string pbuf =  "\"" + cellValue.ToStdString() + "\" & ";
00319         cmd = "openoffice4 -writer " + pbuf;
00320         system(cmd.c_str());
00321     }
00322     else
00323     {
00324         system(cellValue.c_str());
00325     }
00326     // Allow the event to continue to be processed
00327     event.Skip();
00328 }
