00377     query << "SELECT idlk2, lkn, lnk, ddes FROM lk2 WHERE idlk1 = " << idlk1 << " ORDER BY lkn";
00378     mysqlpp::StoreQueryResult res = query.store();
00379     res.num_rows();
00380     if (res.num_rows() > 0)
00381     {
00382         wxString zlabel = std::to_string(res.num_rows()) + " Records Retrieved";
00383         SetStatusText(zlabel);
00384 
00385         detailGrid->ClearGrid();
00386         if (detailGrid->GetNumberRows() >= 1)
00387         {
00388             detailGrid->DeleteRows(0, detailGrid->GetNumberRows());
00389         }
00390         int nrow = res.num_rows();
00391         detailGrid->AppendRows(nrow);
00392 
00393         for (size_t i = 0; i < res.num_rows(); ++i)
00394         {
00395             detailGrid->SetCellValue(i, 0, res[i]["idlk2"].c_str());
00396             detailGrid->SetCellValue(i, 1, res[i]["lkn"].c_str());
00397             detailGrid->SetCellValue(i, 2, res[i]["lnk"].c_str());
00398             detailGrid->SetCellValue(i, 3, res[i]["ddes"].c_str());
00399         }
00400         res.clear();
00401         res.empty();
00402     }
00403     else
00404     {
00405         int id = 1;
00406         wxCommandEvent event(wxEVT_COMMAND_BUTTON_CLICKED, id);
00407 //       wxCommandEvent& event
