00510 void cppMD01Frame::ListFiles(const wxString& dir, const wxString& filespec)
00511 {
00512     wxArrayString *fileList;
00513     fileList->Clear();
00514 
00515     fs::path path = std::string(dir.mb_str());
00516     std::string spec = std::string(filespec.mb_str());
00517 
00518     if (fs::exists(path) && fs::is_directory(path))
00519     {
00520         for (const auto& entry : fs::directory_iterator(path))
00521         {
00522             if (fs::is_regular_file(entry))
00523             {
00524                 std::string filename = entry.path().filename().string();
00525 
00526                 // Apply filespec filtering
00527                 if (boost::iends_with(filename, spec.substr(1)))    // "*.ext" -> "ext"
00528                 {
00529                     fileList->Add(wxString::FromUTF8(filename));
00530                 }
00531             }
00532         }
00533     }
00534 }
